import React, { useEffect, useRef } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-draw/dist/leaflet.draw.css';
import 'leaflet-draw';
import axios from 'axios';

function MapComponent() {
  const mapRef = useRef(null);
  const drawnItems = useRef(null);

  useEffect(() => {
    mapRef.current = L.map("map", {
      center: [0, 0], // 이미지의 중심 좌표 설정
      zoom: 1,        // 이미지가 맵 전체에 꽉 차게 표시되도록 축소 레벨 설정
      crs: L.CRS.Simple, // 이미지 크기를 실제 좌표로 사용하도록 설정
    });

    // B2L2 이미지를 맵에 추가하고 맵의 크기를 이미지에 맞게 조절합니다.
    const b2l2ImageBounds = [
      [0, 0],       // 이미지의 좌측 상단 좌표
      [100, 100]    // 이미지의 우측 하단 좌표 (임의의 값, 이미지 크기에 맞게 조절하세요)
    ];

    L.imageOverlay("images/B2L2.png", b2l2ImageBounds).addTo(mapRef.current);
    mapRef.current.fitBounds(b2l2ImageBounds);

    return () => {
      mapRef.current.remove();
    };
  }, []);

  return (
    <div>
      <NavigationBar />
      <div id="map" style={{ height: "500px" }}></div>
      {userLocation && (
        <div className="navigation-bar">
          <p>
            Your Location: {userLocation[0]}, {userLocation[1]}
          </p>
        </div>
      )}
      {selectedBuilding && (
        <div className="navigation-bar">
          <button onClick={() => setSelectedBuilding(null)}>X</button>
          <h2>{selectedBuilding}</h2>
          <img src={`images/selectedBuilding.jpg`} alt={selectedBuilding} />
          <p>Description of {selectedBuilding}</p>
        </div>
      )}
    </div>
  );
};

export default GeoJSONMap;
