import React, { useState } from "react";
import { Container, Row, Col } from "reactstrap";

import VTlist from "./VTlist";
import VTcontent from "./VTcontent";

import "./VerticalTab.css";
import "./VTlist.css";
import GeoJSONMap from "../GeoJSONMap";

function VerticalTab(props) {
  const [activeTabId, setActiveTabId] = useState(0);

  function btnClick(id) {
    setActiveTabId(id);
    // <GeoJSONMap selected={"building 1"}/>
  }
  const Clicked = () => {
      props.onClick(props.index);
      const setBuilding = props.features.properties.name;
      <GeoJSONMap selected={setBuilding}/>
  };
  

  return (
    <Container className="section__Jobs-container">
      <Row>
        <Col sm="3">
          <div className="section__Jobs-styledTab">
            <ul className="section__Jobs-styledTabList">
              {props.data.map((features, index) => (
                <li key={props.index} style={{ listStyle: "none", textAlign: "left", bottom: 500 }}>
                <button
                  className="section__Jobs-buttonCompany"
                  onClick={Clicked}
                  style={
                    props.activeTabId === props.index
                      ? { color: "#CE3030" }
                      : { color: "#8892b0" }
                  }
                >
                  {props.data.properties.name}
                </button>
              </li>
              ))}
            </ul>
          </div>
        </Col>
        {/* <Col sm="9">
          {props.data.map((features, index) => (
            <VTcontent
              data={features}
              key={index}
              index={index}
              activeTabId={activeTabId}
            />
          ))}
        </Col> */}
      </Row>
      <span
        className={
          activeTabId === 0
            ? "index1-chosen"
            : activeTabId === 1
            ? "index2-chosen"
            : "index3-chosen"
        }
      >
        &nbsp;
      </span>
    </Container>
  );
}

export default VerticalTab;
