// NavigationBar.js

import React, { useState } from "react";

const NavigationBar = ({ handleDirection }) => {
  const [startPoint, setStartPoint] = useState("");
  const [endPoint, setEndPoint] = useState("");

  const handleStartPointChange = (e) => {
    setStartPoint(e.target.value);
  };

  const handleEndPointChange = (e) => {
    setEndPoint(e.target.value);
  };

  const handleDirectionClick = () => {
    // Perform the action for displaying directions on the map
    handleDirection(startPoint, endPoint);
  };

  return (
    <div className="navigation-bar">
      <h3>Navigation</h3>
      <label htmlFor="startPoint">Start Point:</label>
      <select id="startPoint" value={startPoint} onChange={handleStartPointChange}>
        {/* Options for start points */}
        {/* You can populate options dynamically based on your data */}
        <option value="location1">Location 1</option>
        <option value="location2">Location 2</option>
        {/* Add more options as needed */}
      </select>
      <label htmlFor="endPoint">End Point:</label>
      <select id="endPoint" value={endPoint} onChange={handleEndPointChange}>
        {/* Options for end points */}
        {/* You can populate options dynamically based on your data */}
        <option value="destination1">Destination 1</option>
        <option value="destination2">Destination 2</option>
        {/* Add more options as needed */}
      </select>
      <button onClick={handleDirectionClick}>Show Directions</button>
    </div>
  );
};

export default NavigationBar;
