import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import geoJSONData from "./drawn_shape.geojson"; // geojson 파일 경로

const GeoJSONMap = () => {
  useEffect(() => {
    const map = L.map("map").setView([10.7298, 106.69451], 20);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "© OpenStreetMap contributors",
    }).addTo(map);
    console.log(geoJSONData.features);    // 폴리곤들의 좌표를 순회하면서 지도에 추가
    geoJSONData.features.forEach((feature) => {
      const coordinates = feature.geometry.coordinates[0]; // 폴리곤의 좌표

      L.polygon(coordinates, {
        color: "red",
        fillColor: "orange",
        fillOpacity: 0.5,
      }).addTo(map);
    });

    return () => {
      map.remove();
    };
  }, []);

  return (
    <div id="map" style={{ height: "500px" }}>
      {/* 지도를 렌더링할 div 요소 */}
    </div>
  );
};

export default GeoJSONMap;
