import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import geojsonFeature from './drawn_shape.geojson'; // geojson 파일 경로

const GeoJSONMap = () => {
  useEffect(() => {
    const map = L.map('map').setView([10.7298, 106.69451], 20);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors',
    }).addTo(map);
    var geojsonFeature =     {
        "type": "Feature",
        "properties": {
          "name": "building 1"
        },
        "geometry": {
          "type": "Polygon",
          "coordinates": [
            [
              [106.695856, 10.729519],
              [106.695942, 10.729308],
              [106.695829, 10.729266],
              [106.695888, 10.729134],
              [106.696124, 10.729213],
              [106.69628, 10.729203],
              [106.696275, 10.729123],
              [106.696516, 10.729113],
              [106.696516, 10.729203],
              [106.696715, 10.729218],
              [106.696629, 10.729398],
              [106.696699, 10.729445],
              [106.696645, 10.729582],
              [106.696516, 10.729519],
              [106.695856, 10.729519]
            ]
          ]
        }
      }
    L.geoJSON(geojsonFeature).addTo(map);

    return () => {
      map.remove();
    };
  }, []);

  return (
    <div id="map" style={{ height: '500px' }}>
      {/* 지도를 렌더링할 div 요소 */}
    </div>
  );
};

export default GeoJSONMap;
