import React, { useEffect, useRef } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-draw/dist/leaflet.draw.css';
import 'leaflet-draw';
import axios from 'axios';

function MapComponent() {
  const mapRef = useRef(null);
  const drawnItems = useRef(null);

  useEffect(() => {
    mapRef.current = L.map('map', {
      center: [0, 0],
      zoom: 1,
      crs: L.CRS.Simple,
    });

    // B2L2 이미지를 맵에 추가하고 맵의 크기를 이미지에 맞게 조절합니다.
    // 이미지의 원본 크기를 확인하고 아래 좌표를 조절하세요.
    const b2l2ImageBounds = [
      [0, 0],       // 좌측 상단 좌표
      [width, height]    // 우측 하단 좌표
    ];

    L.imageOverlay('images/B2L2.png', b2l2ImageBounds).addTo(mapRef.current);
    mapRef.current.fitBounds(b2l2ImageBounds);

    drawnItems.current = new L.FeatureGroup();
    mapRef.current.addLayer(drawnItems.current);

    const drawControl = new L.Control.Draw({
      edit: {
        featureGroup: drawnItems.current,
      },
      draw: {
        polygon: true,
        polyline: true,
        circle: false,
        marker: true,
        circlemarker: false,
        rectangle: true,
      },
    });
    mapRef.current.addControl(drawControl);

    mapRef.current.on(L.Draw.Event.CREATED, (event) => {
      const layer = event.layer;
      drawnItems.current.addLayer(layer);
    });

    return () => {
      mapRef.current.remove();
    };
  }, []);

  const saveShape = () => {
    if (drawnItems.current) {
      const geoJSON = drawnItems.current.toGeoJSON();
      const stringifiedData = JSON.stringify(geoJSON, null, 2);

      const blob = new Blob([stringifiedData], { type: 'application/json' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'drawn_shape.geojson';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  return (
    <div>
      <div id='map' style={{ height: '500px' }}></div>
      <button onClick={saveShape}>Save Shape</button>
    </div>
  );
}

export default MapComponent;
