import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import geojsonFeature from "./drawn_shape.json"; // geojson 파일 경로
import leafletPip from "leaflet-pip";

const GeoJSONMap = () => {
  useEffect(() => {
    const map = L.map("map").setView([10.7298, 106.69451], 20);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "© OpenStreetMap contributors",
    }).addTo(map);

    // L.geoJSON(geojsonFeature, {
    //     onEachFeature: (feature, layer) => {
    //         const name = feature.properties.name;
    //         layer.bindPopup(name);
    //     }
    // }).addTo(map);

    // 다각형을 저장할 배열 생성
    const buildingPolygons = [];

    // 각 Feature에 스타일 및 팝업 추가하여 지도에 표시
    L.geoJSON(geojsonFeature, {
      onEachFeature: (feature, layer) => {
        const name = feature.properties.name;
        layer.bindPopup(name);

        if (name !== "building 7") {
          // building 7이 아닌 다른 건물일 경우 배열에 추가
          buildingPolygons.push(layer);
        }
      },
    }).addTo(map);

    // building 7 건물 추가
    const building7Layer = buildingPolygons.find(
      (layer) => layer.feature.properties.name === "building 7"
    );
    building7Layer.addTo(map);

    // 클릭 이벤트 처리
    map.on("click", function (e) {
      const clickedBuilding = e.layer.feature.properties.name;

      const insideBuilding7 = leafletPip.pointInLayer(e.latlng, building7Layer);

      if (clickedBuilding === "building 7" || insideBuilding7.length > 0) {
        // 건물 7 또는 건물 7 안에서 클릭한 경우
        building7Layer.bindPopup("Building 7").openPopup();
      } else {
        // 다른 건물을 클릭한 경우
        const clickedLayer = buildingPolygons.find(
          (layer) => layer.feature.properties.name === clickedBuilding
        );
        if (clickedLayer) {
          clickedLayer.bindPopup(clickedBuilding).openPopup();
        }
      }
    });

    return () => {
      map.remove();
    };
  }, []);

  return (
    <div id="map" style={{ height: "500px" }}>
      {/* 지도를 렌더링할 div 요소 */}
    </div>
  );
};

export default GeoJSONMap;
