import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import geojsonFeature from "./drawn_shape.json"; // geojson 파일 경로
import leafletPip from 'leaflet-pip';


const GeoJSONMap = () => {
  useEffect(() => {
    const map = L.map("map").setView([10.7298, 106.69451], 20);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "© OpenStreetMap contributors",
    }).addTo(map);

    // L.geoJSON(geojsonFeature, {
    //     onEachFeature: (feature, layer) => {
    //         const name = feature.properties.name;
    //         layer.bindPopup(name);
    //     }
    // }).addTo(map);

// 각 Feature에 스타일 및 팝업 추가하여 지도에 표시

// 건물 7의 다각형 생성
const building7Polygon = L.geoJSON(geojsonFeature.features.find(feature => feature.properties.name === 'building 7')).getLayers()[0];

// 다른 건물들의 다각형 생성
const otherBuildingPolygons = geojsonFeature.features
  .filter(feature => feature.properties.name !== 'building 7')
  .map(feature => L.geoJSON(feature).getLayers()[0]);

// 건물 7이 다른 건물과 겹치지 않는 부분에 팝업 추가
if (!leafletPip.pointInLayer(building7Polygon.toGeoJSON().geometry.coordinates[0][0], L.layerGroup(otherBuildingPolygons)).length) {
  building7Polygon.bindPopup('Building 7').addTo(map);
}

    return () => {
      map.remove();
    };
  }, []);

  return (
    <div id="map" style={{ height: "500px" }}>
      {/* 지도를 렌더링할 div 요소 */}
    </div>
  );
};

export default GeoJSONMap;
