import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import geojsonFeature from "./drawn_shape.json"; // geojson 파일 경로
import leafletPip from "leaflet-pip";


const GeoJSONMap = () => {
  useEffect(() => {
    const map = L.map("map").setView([10.7298, 106.69451], 20);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "© OpenStreetMap contributors",
    }).addTo(map);

    // L.geoJSON(geojsonFeature, {
    //     onEachFeature: (feature, layer) => {
    //         const name = feature.properties.name;
    //         layer.bindPopup(name);
    //     }
    // }).addTo(map);

// 각 Feature에 스타일 및 팝업 추가하여 지도에 표시
// 건물들의 다각형을 모두 생성하여 배열에 추가
const buildingPolygons = geojsonFeature.features.map(feature => L.geoJSON(feature).getLayers()[0]);

// 모든 다각형을 지도에 추가
buildingPolygons.forEach(polygon => {
    polygon.addTo(map);
    polygon.on('click', function (e) {
      const clickedBuilding = e.target.feature.properties.name;
      const insideBuilding7 = leafletPip.pointInLayer(e.latlng, buildingPolygons.find(polygon => polygon.feature.properties.name === 'building 7'));
      
      if (clickedBuilding === 'building 7' || insideBuilding7.length > 0) {
        // 건물 7 또는 건물 7 안에서 클릭한 경우
        this.bindPopup('Building 7').openPopup();
      } else {
        // 다른 건물을 클릭한 경우
        this.bindPopup(clickedBuilding).openPopup();
      }
    });
  });


    return () => {
      map.remove();
    };
  }, []);

  return (
    <div id="map" style={{ height: "500px" }}>
      {/* 지도를 렌더링할 div 요소 */}
    </div>
  );
};

export default GeoJSONMap;
