//geojson으로 다시 도전
import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import geojsonFeature from "./drawn_shape.geojson"; // geojson 파일 경로

const GeoJSONMap = () => {
  useEffect(() => {
    const map = L.map("map").setView([10.7298, 106.69451], 20);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "© OpenStreetMap contributors",
    }).addTo(map);

    // geojsonFeature가 로드되었는지 확인
    console.log(geojsonFeature);

    geojsonFeature.features.forEach((feature) => {
      L.geoJSON(feature.geometry).addTo(map);
    });

    return () => {
      map.remove();
    };
  }, [geojsonFeature]); // geojsonFeature를 의존성 배열에 추가하여 업데이트 시에 useEffect를 다시 실행

  return (
    <div id="map" style={{ height: "500px" }}>
      {/* 지도를 렌더링할 div 요소 */}
    </div>
  );
};

export default GeoJSONMap;
