// GeoJSONMap.js

import React, { useEffect, useState, useRef } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import buildingCollection from "./building_info/buildingCollection";

function GeoJSONMap() {
  const mapRef = useRef(null);
  const [selectedBuilding, setSelectedBuilding] = useState(null);

  useEffect(() => {
    mapRef.current = L.map("map", {
      center: [10.7298, 106.69451],
      zoom: 20,
    });

    L.tileLayer("images/B2L2.png", {}).addTo(mapRef.current);

    const layers = L.geoJSON(buildingCollection, {
      style: {
        fillColor: "blue",
        color: "black",
        weight: 1,
        fillOpacity: 0.3,
      },
      onEachFeature: (feature, layer) => {
        layer.bindPopup(feature.properties.name);
      },
    });

    layers.addTo(mapRef.current);

    mapRef.current.on("click", function (e) {
      const clickedLatLng = e.latlng;
      const clickedBuilding = layers.getLayers().find((layer) =>
        layer.getBounds().contains(clickedLatLng)
      );

      if (clickedBuilding) {
        const clickedBuildingName = clickedBuilding.feature.properties.name;
        clickedBuilding.bindPopup(clickedBuildingName).openPopup();
        setSelectedBuilding(clickedBuildingName);
      } else {
        setSelectedBuilding(null);
      }
    });

    return () => {
      mapRef.current.remove();
    };
  }, []);

  return (
    <div>
      <div id="map" style={{ height: "500px" }}></div>
      <div>
        <h3>Selected Building:</h3>
        <p>{selectedBuilding || "No building selected"}</p>
      </div>
    </div>
  );
}

export default GeoJSONMap;
