import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

function MapComponent() {
  const [coordinates, setCoordinates] = useState([]);

  useEffect(() => {
    axios.get('/api/coordinates') // 예시: 데이터베이스에서 좌표 정보를 가져오는 API 엔드포인트
      .then(response => {
        setCoordinates(response.data); // 받아온 좌표 정보를 상태에 저장
      })
      .catch(error => {
        console.error('Error fetching coordinates:', error);
      });
  }, []);

  useEffect(() => {
    const map = L.map('map').setView([10.7369, 106.6773], 13); // RMIT Vietnam Ho Chi Minh Campus coordinates

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors',
    }).addTo(map);

    // 좌표 정보가 있는 경우 Polygon 그리기
    if (coordinates.length > 0) {
      const polygon = L.polygon(coordinates, { color: 'red' }).addTo(map);
      map.fitBounds(polygon.getBounds());
    }
  }, [coordinates]);

  return <div id="map" style={{ height: '500px' }}></div>;
}

export default MapComponent;
