// MapComponent.js
import React, { useEffect, useRef, useState } from "react";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import "leaflet-draw/dist/leaflet.draw.css";
import "leaflet-draw";
import axios from "axios";

function MapComponent({ buildingCollection, setBuildingCollection }) {
  const mapRef = useRef(null);
  const drawnItems = useRef(null);
  const [geoJsonData, setGeoJsonData] = useState(null);

  useEffect(() => {
    if (!mapRef.current) {
      const map = L.map("map", { drawControl: true }).setView(
        [37.5665, 126.9780],
        13
      );

      const imageUrl = "images/B2L2.png";
      const imageBounds = [
        [37.527, 126.83],
        [37.608, 127.13],
      ];

      L.imageOverlay(imageUrl, imageBounds).addTo(map);

      drawnItems.current = new L.FeatureGroup();
      map.addLayer(drawnItems.current);

      const drawControl = new L.Control.Draw({
        edit: {
          featureGroup: drawnItems.current,
        },
        draw: {
          polygon: true,
          polyline: true,
          circle: false,
          marker: true,
          circlemarker: false,
          rectangle: true,
        },
      });
      map.addControl(drawControl);

      mapRef.current = map;

      map.on(L.Draw.Event.CREATED, (event) => {
        const layer = event.layer;
        drawnItems.current.addLayer(layer);
      });
    }
  }, []);

  const saveShape = () => {
    if (drawnItems.current) {
      const geoJSON = drawnItems.current.toGeoJSON();
      const stringifiedData = JSON.stringify(geoJSON, null, 2);

      // 여기서 서버로 stringifiedData를 보내어 파일로 저장하고, 파일의 경로를 받아오는 로직을 구현
      axios
        .post("/api/save-shape", { shapeData: geoJSON })
        .then((response) => {
          console.log(response.data.message);

          // 받아온 파일 경로로 다시 데이터를 불러오는 로직을 구현
          axios.get(response.data.filePath).then((fileResponse) => {
            setGeoJsonData(fileResponse.data);
            setBuildingCollection(fileResponse.data);
          });
        })
        .catch((error) => {
          console.error("Error saving shape:", error);
        });
    }
  };

  return (
    <div>
      <div id="map" style={{ height: "620px", margin: "auto" }}></div>
      <button onClick={saveShape}>Save Shape</button>
    </div>
  );
}

export default MapComponent;
