import React, { useEffect, useState, useRef } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-draw/dist/leaflet.draw.css';
import 'leaflet-draw';
import axios from 'axios';

function MapComponent() {
    const mapRef = useRef(null);
    const [drawnItems, setDrawnItems] = useState(null);
  
    useEffect(() => {
      if (!mapRef.current) {
        const map = L.map('map').setView([10.7298, 106.69451], 20);
  
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '© OpenStreetMap contributors',
        }).addTo(map);
  
        const drawnItems = L.featureGroup(); // FeatureGroup로 변경
        map.addLayer(drawnItems);
  
        const drawControl = new L.Control.Draw({
          edit: {
            featureGroup: drawnItems,
          },
          draw: {
            polygon: true,
            polyline: false,
            circle: false,
            marker: false,
            circlemarker: false,
            rectangle: true,
          },
        });
        map.addControl(drawControl);

        mapRef.current = map;
        setDrawnItems(drawnItems); // drawnItems 상태에 FeatureGroup 할당
      }

      mapRef.current.on(L.Draw.Event.CREATED, (event) => {
        const layer = event.layer;
        drawnItems.addLayer(layer);
      });
    }, []);
  
    const sendDataToServer = () => {
      if (drawnItems) {
        const geoJSON = drawnItems.toGeoJSON(); // toGeoJSON 호출
        axios.post('/api/save-shape', { shapeData: geoJSON })
          .then(response => {
            console.log(response.data.message);
          })
          .catch(error => {
            console.error('Error saving shape:', error);
          });
      }
    };
  
    return (
      <div>
        <div id="map" style={{ height: '500px' }}></div>
        <button onClick={sendDataToServer}>Save Shape</button>
      </div>
    );
}

export default MapComponent;
